import React, { useEffect } from 'react';
import Axios from 'axios';
import { withStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';

const styles = (theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(2),
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(1),
        top: theme.spacing(1),
        color: theme.palette.grey[500],
    },
});

const DialogTitle = withStyles(styles)((props) => {
    const { children, classes, onClose, ...other } = props;
    return (
        <MuiDialogTitle disableTypography className={classes.root} {...other}>
            <Typography variant="h6">{children}</Typography>
            {onClose ? (
                <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                    <CloseIcon />
                </IconButton>
            ) : null}
        </MuiDialogTitle>
    );
});

const DialogContent = withStyles((theme) => ({
    root: {
        padding: theme.spacing(2),
    },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(1),
    },
}))(MuiDialogActions);

function Test() {
    // const [open, setOpen] = React.useState(false);
    const [Subject, setSubject] = React.useState(0);
    const [Questiontem, setQuestiontem] = React.useState('');
    const [Answertem, setAnswertem] = React.useState('');
    const [Questionarray, setQuestionarray] = React.useState([]);
    const [Answerarray, setAnswerarray] = React.useState([]);
    const [Subjectarray, setSubjectarray] = React.useState([]);
    const [Newques, setNewques] = React.useState(true);
    const cancelquestion = () => {

    }

    useEffect(() => {
        Axios.get(`http://localhost:5000/admin/getsubject`)
            .then(res => {
                console.log(res.data.data);
                setSubjectarray(res.data.data);

            })
    }, [])

    const handlequestion = () => {
        console.log(Questiontem, Answertem);
        setQuestionarray(...Questionarray, Questiontem);
        setAnswerarray(...Answerarray, Answertem);

        console.log(Questionarray, Answerarray);
    }
    const handleChange = (event) => {
        setSubject(event.target.value);
    };
    const handleQChange = (event) => {
        setQuestiontem(event.target.value);
    }
    const handleAChange = (event) => {
        setAnswertem(event.target.value);
    }

    const addques = () => {
        setNewques(true);
        console.log(Newques);
        let rows = [];
        while (Newques === true) {
            rows.push(
                <div>
                    <textarea name="questionhead" cols="50" rows="2" onChange={handleQChange} value={Questiontem} placeholder="Question"></textarea>
                    <input type="text" name="answer" value={Answertem} onChange={handleAChange} />
                    <Button onClick={handlequestion} color="secondary">Submit Question</Button>
                    <Button onClick={cancelquestion} color="secondary">Cancel</Button>
                </div>)
        }
        setNewques(false);
        console.log(Newques);
        return rows;
    }

    return (
        <div>
            <div>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={Subject}
                    onChange={handleChange}
                >
                    {Subjectarray.map((data) =>
                        (<MenuItem value={data.subject_id}>{data.subject_name}</MenuItem>))
                    }
                </Select>
                <Button onClick={addques} color="primary">Add Question</Button>
            </div>
        </div>
    );
}
export default Test;
