import React from 'react';
import Axios from 'axios';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';

const styles = (theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(2),
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(1),
        top: theme.spacing(1),
        color: theme.palette.grey[500],
    },
});

const DialogTitle = withStyles(styles)((props) => {
    const { children, classes, onClose, ...other } = props;
    return (
        <MuiDialogTitle disableTypography className={classes.root} {...other}>
            <Typography variant="h6">{children}</Typography>
            {onClose ? (
                <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                    <CloseIcon />
                </IconButton>
            ) : null}
        </MuiDialogTitle>
    );
});

const DialogContent = withStyles((theme) => ({
    root: {
        padding: theme.spacing(2),
    },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(1),
    },
}))(MuiDialogActions);

export default function Test() {
    const [open, setOpen] = React.useState(false);
    const [Subject, setSubject] = React.useState(0);
    const [Questiontem, setQuestiontem] = React.useState('');
    const [Answertem, setAnswertem] = React.useState('');
    const [Option1tem, setOption1tem] = React.useState('');
    const [Option2tem, setOption2tem] = React.useState('');
    const [Questionarray, setQuestionarray] = React.useState([]);
    const [Answerarray, setAnswerarray] = React.useState([]);
    const [Option1array, setOption1array] = React.useState([]);
    const [Option2array, setOption2array] = React.useState([]);
    const [Subjectarray, setSubjectarray] = React.useState([]);
    const [Newques, setNewques] = React.useState(true);
    useEffect(() => {
        Axios.get(`http://localhost:5000/admin/getsubject`)
            .then(res => {
                console.log(res.data.data);
                setSubjectarray(res.data.data);

            })
    }, [])
    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
    };
    const handlequestion = () => {
        console.log(Questiontem, Answertem, Option1tem, Option2tem);
        setQuestionarray(...Questionarray, Questiontem);
        setAnswerarray(...Answerarray, Answertem);
        setOption1array(...Option1array, Option1tem);
        setOption2array(...Option2array, Option2tem);
        console.log(Questionarray, Answerarray, Option1array, Option2array);
    }
    const handleChange = (event) => {
        setSubject(event.target.value);
    };
    const handleQChange = (event) => {
        setQuestiontem(event.target.value);
    }
    const handleAChange = (event) => {
        setAnswertem(event.target.value);
    }
    const handle1Change = (event) => {
        setOption1tem(event.target.value);
    }
    const handle2Change = (event) => {
        setOption2tem(event.target.value);
    }
    const addques = () => {
        setNewques(true);
        console.log(Newques);
        let rows = [];
        while (Newques === true) {
            rows.push(<div>
                <DialogContent dividers>
                    <TextField id="standard-basic" value={Questiontem} onChange={handleQChange} label="Question" />
                    <TextField id="standard-basic" value={Answertem} onChange={handleAChange} label="Answer" />
                    <TextField id="standard-basic" value={Option1tem} onChange={handle1Change} label="option 1" />
                    <TextField id="standard-basic" value={Option2tem} onChange={handle2Change} label="option 2" />
                </DialogContent>
