import React from "react";
import "./signupdiv.css";
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

// import { Redirect } from 'react0-router-dom';
// import history from './history';
// import axios from "axios";

// const { pool, Client } = require('pg');

class SignupDiv extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            email: "",
            role: "",
            stname: "",
            password: "",
            cpassword: "",
            isteacher: true,
            namearray: [],
            image: null,
            imge: false,
            file: null
        };
    }
    handleChange = (e) => {

        this.setState({ [e.target.name]: e.target.value });

    };
    render() {
        return (
            <div align="center">
                <form className="signup">
                    <input
                        className="form-alignment"
                        type="text"
                        id="name"
                        name="name"
                        placeholder="Name"
                        className="form-alignments"
                        onChange={e => this.handleChange(e)}
                    />
                    <input
                        type="email"
                        id="email"
                        name="email"
                        placeholder="Email"
                        className="form-alignments"
                        onChange={e => this.handleChange(e)}
                    />
                    {/* <div className="col-25">
                        <label htmlFor="fname">Create an account as ?</label>
                    </div>
                    <div className="col-75">
                        <select id="role" className="selectstyle" name="role">
                            <option value="Teacher">Teacher</option>
                            <option value="Student">Student</option>

                        </select>
                    </div> */}
                    <div align="center" className="selectset">
                        <InputLabel id="demo-customized-select-label">Create an account as ?</InputLabel>
                        <Select
                            labelId="demo-customized-select-label"
                            id="demo-customized-select"
                            value={this.state.role}
                            onChange={e => this.handleChange(e)}
                        >
                            <MenuItem value="">
                                <em>None</em>
                            </MenuItem>
                            <MenuItem value={"teacher"}>teacher</MenuItem>
                            <MenuItem value={"student"}>student</MenuItem>
                            {/* <MenuItem value={30}>Thirty</MenuItem> */}
                        </Select>
                    </div>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        placeholder="Password"
                        className="form-alignments"
                        onChange={e => this.handleChange(e)}
                    />
                    <input
                        type="password"
                        id="confirm"
                        name="cpassword"
                        placeholder="Confirm Password"
                        className="form-alignments"
                        onChange={e => this.handleChange(e)}
                    />
                    <button
                        className="buttonstyle"
                        id="send"
                        onClick={e => {
                            this.signup(e);
                        }}>
                        Signup
            </button>
                    <div className="buttins">
                        <p className="para">Have an account?</p>
                        <button className="buttonstyle">
                            <a className="line" href="/">
                                Login
                </a>
                        </button>
                    </div>
                </form>
            </div>)
    }
}
export default SignupDiv;
