import { makeStyles, useTheme, withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import React, { useEffect } from 'react';
import clsx from 'clsx';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Paper from '@material-ui/core/Paper';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import AssignmentTurnedInIcon from '@material-ui/icons/AssignmentTurnedIn';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import ListItemText from '@material-ui/core/ListItemText';
import './Adminhome.css';
import Axios from 'axios';
const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({

    appBar: {
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
    },
    body: {
        fontSize: 14,
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    hide: {
        display: 'none',
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
}));



export default function AdminHome() {

    // const StyledTableRow = withStyles((theme) => ({
    //     root: {
    //         '&:nth-of-type(odd)': {
    //             backgroundColor: theme.palette.action.hover,
    //         },
    //     },
    // }))(TableRow);
    const StyledTableCell = withStyles((theme) => ({
        head: {
            backgroundColor: theme.palette.common.black,
            color: theme.palette.common.white,
        },
        body: {
            fontSize: 14,
        },
    }))(TableCell);



    const StyledTableRow = withStyles((theme) => ({
        root: {
            '&:nth-of-type(odd)': {
                backgroundColor: theme.palette.action.hover,
            },
        },
    }))(TableRow);
    const classes = useStyles();
    const theme = useTheme();
    const [open, setOpen] = React.useState(false);
    const [Accessarray, setAccessarray] = React.useState([]);
    const [Assignarray, setAssignarray] = React.useState([]);
    const [Subjectarray, setSubjectarray] = React.useState([]);
    const [Accessview, setAccessview] = React.useState(false);
    const [Assignview, setAssignview] = React.useState(false);
    const accessset = () => {
        setAccessview(true);
    }
    const assignset = () => {
        setAssignview(true);
    }
    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };
    useEffect(() => {
        Axios.get(`http://localhost:5000/getaccesslist`)
            .then(res => {
                console.log(res.data.data);
                setAccessarray(res.data.data);
                // console.log(Commentarray);
            })
        Axios.get(`http://localhost:5000/getassignlist`)
            .then(res => {
                console.log(res.data.data);
                setAssignarray(res.data.data);
                // console.log(Answerarray);
            })
        Axios.get(`http://localhost:5000/getsubject`)
            .then(res => {
                console.log(res.data.data);
                setSubjectarray(res.data.data);

            })

    }, [])
    return (
        <div>
            <div className='drawersetup'>
                <CssBaseline />
                <AppBar
                    position="fixed"
                    className={clsx(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={handleDrawerOpen}
                            edge="start"
                            className={clsx(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Testing Engine
          </Typography>
                    </Toolbar>
                </AppBar>
                <Drawer className={classes.drawer}
                    variant="persistent"
                    anchor="left"
                    open={open}
                    classes={{
                        paper: classes.drawerPaper,
                    }}>
                    <div className={classes.drawerHeader}>
                        <AccountCircleIcon></AccountCircleIcon><span>Admin</span>
                        <IconButton onClick={handleDrawerClose}>
                            {theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
                        </IconButton>
                    </div>
                    <Divider />
                    <List>
                        <ListItem button onClick={accessset()} key={'Student Access'}>
                            <ListItemIcon>< AccountCircleIcon /></ListItemIcon>
                            <ListItemText primary={'Student Access'} />
                        </ListItem>
                        <ListItem button onClick={assignset()} key={'Subject Assignment'}>
                            <ListItemIcon><AssignmentTurnedInIcon /></ListItemIcon>
                            <ListItemText primary={'Subject Assignment'} />
                        </ListItem>

                    </List>
                </Drawer>

            </div>
            <div>
                {Assignview ?
                    <TableContainer component={Paper}>
                        <Table className={classes.table} aria-label="customized table"><TableHead>
                            <TableRow>
                                <StyledTableCell>Student ID</StyledTableCell>
                                <StyledTableCell align="right">Student Name</StyledTableCell>
                                <StyledTableCell align="right">Student Email</StyledTableCell>
                                <StyledTableCell align="right">Student Password</StyledTableCell>
                                <StyledTableCell align="right">Provide Access</StyledTableCell>
                            </TableRow>
                        </TableHead>
                            <TableBody>
                                {Accessarray.map((row) => (
                                    <StyledTableRow key={row.name}>
                                        <StyledTableCell component="th" scope="row">
                                            {row.name}
                                        </StyledTableCell>
                                        <StyledTableCell align="right">{row.calories}</StyledTableCell>
                                        <StyledTableCell align="right">{row.fat}</StyledTableCell>
                                        <StyledTableCell align="right">{row.carbs}</StyledTableCell>
                                        <StyledTableCell align="right">{row.protein}</StyledTableCell>
                                    </StyledTableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer> : <></>
                }

            </div >

        </div >
    )
}
